export const makeKey = (...inputs) => inputs.filter(input => !!input).join('.');
export function convertToDefineObject(data) {
    const convertToEntries = (data, prefix = '') => {
        return Object.entries(data).reduce((entries, [key, value]) => {
            const newKey = makeKey(prefix, key);
            if (value && typeof value === 'object' && !Array.isArray(value)) {
                entries.push(...convertToEntries(value, newKey));
            }
            else {
                entries.push([newKey, JSON.stringify(value) ?? String(undefined)]);
            }
            return entries;
        }, []);
    };
    return Object.fromEntries(convertToEntries(data));
}
